$(document).ready(function(){
	$('.mobile_icon').on('click', function(){
		$('.mobile_bg').fadeIn(function(){
			$('.mobile_menu').animate({'left':'0'});
		});
	});

	$('.close_icon, .mobile_bg').on('click', function(){
		$('.mobile_menu').animate({'left':'-275px'});
		$('.mobile_bg').fadeOut(2000);
	});
});